import React, { useState, useEffect } from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faTrash, faEdit, faPlus } from "@fortawesome/free-solid-svg-icons";
import Alert from "./Alert";
import BackendService from "../services/BackendService";
import PaginationComponent from "./PaginationComponent";
import { useNavigate } from "react-router-dom";

const CountryListComponent = (props) => {
  const [message, setMessage] = useState();
  const [countries, setCountries] = useState([]);
  const [selectedCountries, setSelectedCountries] = useState([]);
  const [show_alert, setShowAlert] = useState(false);
  const [checkedItems, setCheckedItems] = useState([]);
  const [hidden, setHidden] = useState(false);
  const navigate = useNavigate();
  const [page, setPage] = useState(0);
  const [totalCount, setTotalCount] = useState(0);
  const limit = 2;

  const setChecked = (v) => {
    setCheckedItems(Array(countries.length).fill(v));
  };

  const handleCheckChange = (e) => {
    const idx = e.target.name;
    const isChecked = e.target.checked;

    let checkedCopy = [...checkedItems];
    checkedCopy[idx] = isChecked;
    setCheckedItems(checkedCopy);
  };

  const handleGroupCheckChange = (e) => {
    const isChecked = e.target.checked;
    setChecked(isChecked);
  };

  const deleteCountriesClicked = () => {
    let x = [];
    countries.map((t, idx) => {
      if (checkedItems[idx]) {
        x.push(t);
      }
      return 0;
    });
    if (x.length > 0) {
      var msg;
      if (x.length > 1) {
        msg = "Пожалуйста подтвердите удаление " + x.length + " стран";
      } else {
        msg = "Пожалуйста подтвердите удаление страны " + x[0].name;
      }
      setShowAlert(true);
      setSelectedCountries(x);
      setMessage(msg);
    }
  };

  const refreshCountries = (currentPage) => {
    BackendService.retrieveAllCountries(currentPage, limit)
      .then((resp) => {
        setCountries(resp.data.content);
        console.log(resp);
        setHidden(false);
        setTotalCount(resp.data.totalElements);
        setPage(currentPage);
      })
      .catch(() => {
        setHidden(true);
        setTotalCount(0);
      })
      .finally(() => setChecked(false));
  };

  useEffect(() => {
    refreshCountries(page);
  }, [page]);

  const updateCountryClicked = (id) => {
    navigate(`/countries/${id}`);
  };

  const onDelete = () => {
    BackendService.deleteCountries(selectedCountries)
      .then(() => refreshCountries(page))
      .catch(() => {});
  };

  const closeAlert = () => {
    setShowAlert(false);
  };

  const addCountryClicked = () => {
    navigate(`/countries/-1`);
  };

  const onPageChanged = (currentPage) => {
    refreshCountries(currentPage - 1);
  };

  if (hidden) return null;
  return (
    <div className="m-4">
      <div className="row my-2">
        <h3>Страны</h3>
        <div className="btn-toolbar">
          <div className="btn-group ms-auto">
            <button
              className="btn btn-outline-secondary"
              onClick={addCountryClicked}
            >
              <FontAwesomeIcon icon={faPlus} /> Добавить
            </button>
          </div>
          <div className="btn-group ms-2">
            <button
              className="btn btn-outline-secondary"
              onClick={deleteCountriesClicked}
            >
              <FontAwesomeIcon icon={faTrash} /> Удалить
            </button>
          </div>
        </div>
      </div>
      <div className="row my-2 me-0">
        <PaginationComponent
          totalRecords={totalCount}
          pageLimit={limit}
          pageNeighbours={1}
          onPageChanged={onPageChanged}
        />
        <table className="table table-sm">
          <thead className="thead-light">
            <tr>
              <th>Название</th>
              <th>
                <div className="btn-toolbar pb-1">
                  <div className="btn-group  ms-auto">
                    <input type="checkbox" onChange={handleGroupCheckChange} />
                  </div>
                </div>
              </th>
            </tr>
          </thead>
          <tbody>
            {countries &&
              countries.map((country, index) => (
                <tr key={country.id}>
                  <td>{country.name}</td>
                  <td>
                    <div className="btn-toolbar">
                      <div className="btn-group  ms-auto">
                        <button
                          className="btn btn-outline-secondary btn-sm btn-toolbar"
                          onClick={() => updateCountryClicked(country.id)}
                        >
                          <FontAwesomeIcon icon={faEdit} fixedWidth />
                        </button>
                      </div>
                      <div className="btn-group  ms-2  mt-1">
                        <input
                          type="checkbox"
                          name={index}
                          checked={
                            checkedItems.length > index
                              ? checkedItems[index]
                              : false
                          }
                          onChange={handleCheckChange}
                        />
                      </div>
                    </div>
                  </td>
                </tr>
              ))}
          </tbody>
        </table>
      </div>
      <Alert
        title="Удаление"
        message={message}
        ok={onDelete}
        close={closeAlert}
        modal={show_alert}
        cancelButton={true}
      />
    </div>
  );
};

export default CountryListComponent;
