import React, { useState, useEffect } from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faTrash, faEdit, faPlus } from "@fortawesome/free-solid-svg-icons";
import Alert from "../Alert";
import BackendService from "../../services/BackendService";
import PaginationComponent from "../PaginationComponent";
import { useNavigate } from "react-router-dom";

const UserListComponent = (props) => {
  const [message, setMessage] = useState();
  const [users, setUsers] = useState([]);
  const [selectedUsers, setSelectedUsers] = useState([]);
  const [show_alert, setShowAlert] = useState(false);
  const [checkedItems, setCheckedItems] = useState([]);
  const [hidden, setHidden] = useState(false);
  const navigate = useNavigate();
  const [page, setPage] = useState(0);
  const [totalCount, setTotalCount] = useState(0);
  const limit = 5;

  const setChecked = (v) => {
    setCheckedItems(Array(users.length).fill(v));
  };

  const handleCheckChange = (e) => {
    const idx = e.target.name;
    const isChecked = e.target.checked;

    let checkedCopy = [...checkedItems];
    checkedCopy[idx] = isChecked;
    setCheckedItems(checkedCopy);
  };

  const handleGroupCheckChange = (e) => {
    const isChecked = e.target.checked;
    setChecked(isChecked);
  };

  const deleteUsersClicked = () => {
    let x = [];
    users.map((t, idx) => {
      if (checkedItems[idx]) {
        x.push(t);
      }
      return 0;
    });
    if (x.length > 0) {
      var msg;
      if (x.length > 1) {
        msg = "Пожалуйста подтвердите удаление " + x.length + " пользователя";
      } else {
        msg = "Пожалуйста подтвердите удаление пользователя " + x[0].name;
      }
      setShowAlert(true);
      setSelectedUsers(x);
      setMessage(msg);
    }
  };

  const refreshUsers = (currentPage) => {
    BackendService.retrieveAllUsers(currentPage, limit)
      .then((resp) => {
        setUsers(resp.data.content);
        setHidden(false);
        setTotalCount(resp.data.totalElements);
        setPage(currentPage);
      })
      .catch(() => {
        setHidden(true);
        setTotalCount(0);
      })
      .finally(() => setChecked(false));
  };

  useEffect(() => {
    refreshUsers(page);
  }, [page]);

  const updateUserClicked = (id) => {
    navigate(`/users/${id}`);
  };

  const onDelete = () => {
    BackendService.deleteUsers(selectedUsers)
      .then(() => refreshUsers(page))
      .catch(() => {});
  };

  const closeAlert = () => {
    setShowAlert(false);
  };

  const addUserClicked = () => {
    navigate(`/users/-1`);
  };

  const onPageChanged = (currentPage) => {
    refreshUsers(currentPage - 1);
  };

  if (hidden) return null;
  return (
    <div className="m-4">
      <div className="row my-2">
        <h3>Пользователя</h3>
        <div className="btn-toolbar">
          <div className="btn-group ms-auto">
            <button
              className="btn btn-outline-secondary"
              onClick={addUserClicked}
            >
              <FontAwesomeIcon icon={faPlus} /> Добавить
            </button>
          </div>
          <div className="btn-group ms-2">
            <button
              className="btn btn-outline-secondary"
              onClick={deleteUsersClicked}
            >
              <FontAwesomeIcon icon={faTrash} /> Удалить
            </button>
          </div>
        </div>
      </div>
      <div className="row my-2 me-0">
        <PaginationComponent
          totalRecords={totalCount}
          pageLimit={limit}
          pageNeighbours={1}
          onPageChanged={onPageChanged}
        />
        <table className="table table-sm table-hover table-light">
          <thead className="table-active">
            <tr>
              <th>Логин</th>
              <th> E-mail</th>
              <th>
                <div className="btn-toolbar pb-1">
                  <div className="btn-group  ms-auto">
                    <input type="checkbox" onChange={handleGroupCheckChange} />
                  </div>
                </div>
              </th>
            </tr>
          </thead>
          <tbody>
            {users &&
              users.map((user, index) => (
                <tr key={user.id}>
                  <td>{user.login}</td>
                  <td>{user.email}</td>
                  <td>
                    <div className="btn-toolbar">
                      <div className="btn-group  ms-auto">
                        <button
                          className="btn btn-outline-secondary btn-sm btn-toolbar"
                          onClick={() => updateUserClicked(user.id)}
                        >
                          <FontAwesomeIcon icon={faEdit} fixedWidth />
                        </button>
                      </div>
                      <div className="btn-group  ms-2  mt-1">
                        <input
                          type="checkbox"
                          name={index}
                          checked={
                            checkedItems.length > index
                              ? checkedItems[index]
                              : false
                          }
                          onChange={handleCheckChange}
                        />
                      </div>
                    </div>
                  </td>
                </tr>
              ))}
          </tbody>
        </table>
      </div>
      <Alert
        title="Удаление"
        message={message}
        ok={onDelete}
        close={closeAlert}
        modal={show_alert}
        cancelButton={true}
      />
    </div>
  );
};

export default UserListComponent;
